import React from 'react';
import { Box, IconButton, Typography, Button } from '@mui/material';
import SettingsIcon from '@mui/icons-material/Settings';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { markAttendance } from '../../redux/slices/attendanceSlice';

const PASSWORD = '1234'; // 관리자 비밀번호

function AttendanceButton() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const todayCount = useSelector(state => state.attendance.todayCount || 0);
  const students = JSON.parse(localStorage.getItem('students') || '[]');

  // 현재 날짜와 시간 정보
  const now = new Date();
  const days = ['일', '월', '화', '수', '목', '금', '토'];
  const currentDay = days[now.getDay()];
  const currentDate = `${now.getFullYear()}년 ${now.getMonth() + 1}월 ${now.getDate()}일`;
  const currentTime = now.toLocaleTimeString('ko-KR', {
    hour: '2-digit',
    minute: '2-digit',
    hour12: true
  });

  const handleSettingsClick = () => {
    const password = prompt('비밀번호를 입력하세요:');
    if (password === PASSWORD) {
      navigate('/management');
    } else if (password !== null) {
      alert('비밀번호가 올바르지 않습니다.');
    }
  };

  const handleAttendanceClick = () => {
    const today = new Date().getDay();
    const dayMap = {0: '일', 1: '월', 2: '화', 3: '수', 4: '목', 5: '금', 6: '토'};
    const todayStudents = students.filter(student => student.day === dayMap[today]);

    if (todayStudents.length === 0) {
      alert('오늘은 등록된 수업이 없습니다.');
      return;
    }

    const oneOnOneCount = todayStudents.filter(student => student.isOneOnOne).length;
    const regularCount = todayStudents.filter(student => !student.isOneOnOne).length;
    const totalCount = oneOnOneCount * 2 + regularCount;

    if (window.confirm('출석을 체크하시겠습니까?')) {
      dispatch(markAttendance());
      alert(`오늘 출석: ${todayCount + 1}명 (1:1 수업 ${oneOnOneCount}명 포함)`);
    }
  };

  return (
    <Box 
      sx={{ 
        minHeight: '100vh',
        bgcolor: '#FAFAFA',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        position: 'relative',
        pt: 2
      }}
    >
      {/* Header */}
      <Box
        sx={{
          width: '100%',
          bgcolor: 'white',
          borderBottom: '1px solid #DBDBDB',
          py: 2,
          px: 3,
          position: 'sticky',
          top: 0,
          zIndex: 1000,
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        <Typography
          variant="h6"
          sx={{
            background: 'linear-gradient(45deg, #405DE6, #5851DB, #833AB4, #C13584, #E1306C, #FD1D1D)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 600,
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
          }}
        >
          Kevin's Class
        </Typography>
        <IconButton 
          onClick={handleSettingsClick}
          sx={{
            color: '#262626',
          }}
        >
          <SettingsIcon />
        </IconButton>
      </Box>

      {/* Main Content */}
      <Box
        sx={{
          width: '100%',
          maxWidth: 600,
          p: 2,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: 3,
          mt: 3,
        }}
      >
        {/* Date and Time Display */}
        <Box
          sx={{
            width: '100%',
            p: 3,
            bgcolor: 'white',
            borderRadius: 3,
            border: '1px solid #DBDBDB',
            textAlign: 'center',
          }}
        >
          <Typography
            variant="h6"
            sx={{
              fontWeight: 600,
              color: '#262626',
              mb: 1,
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            }}
          >
            {currentDate} ({currentDay}요일)
          </Typography>
          <Typography
            variant="h4"
            sx={{
              fontWeight: 700,
              color: '#262626',
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            }}
          >
            {currentTime}
          </Typography>
        </Box>

        <Button
          onClick={handleAttendanceClick}
          variant="contained"
          sx={{
            width: '100%',
            height: 200,
            borderRadius: 3,
            background: 'linear-gradient(45deg, #405DE6, #5851DB, #833AB4, #C13584, #E1306C, #FD1D1D)',
            fontSize: '1.5rem',
            fontWeight: 600,
            color: 'white',
            textTransform: 'none',
            '&:hover': {
              background: 'linear-gradient(45deg, #833AB4, #C13584, #E1306C, #FD1D1D, #F77737, #FCAF45)',
            },
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
          }}
        >
          출석체크
        </Button>

        <Box
          sx={{
            width: '100%',
            p: 3,
            bgcolor: 'white',
            borderRadius: 3,
            border: '1px solid #DBDBDB',
            textAlign: 'center',
          }}
        >
          <Typography
            variant="h5"
            sx={{
              fontWeight: 600,
              color: '#262626',
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            }}
          >
            오늘 출석: {todayCount}명
          </Typography>
        </Box>
      </Box>
    </Box>
  );
}

export default AttendanceButton;
