import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormControlLabel,
  Checkbox,
  Button,
  Divider,
  Tabs,
  Tab,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Container,
  Paper
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { useDispatch } from 'react-redux';
import { endDay, endWeek } from '../../redux/slices/attendanceSlice';
import { useNavigate } from 'react-router-dom';

const DAYS = ['월', '화', '수', '목', '금', '토'];
const TIMES = Array.from({ length: 12 }, (_, i) => {
  const hour = Math.floor(i / 2) + 14; // 14:00부터 시작
  const minute = i % 2 === 0 ? '00' : '30';
  return `${hour}:${minute}`;
});

function ManagementPanel() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [currentTab, setCurrentTab] = useState(0);
  const [students, setStudents] = useState([]);
  const [studentForm, setStudentForm] = useState({
    name: '',
    day: '',
    time: '',
    isOneOnOne: false
  });

  useEffect(() => {
    const savedStudents = JSON.parse(localStorage.getItem('students') || '[]');
    setStudents(savedStudents);
  }, []);

  const handleTabChange = (event, newValue) => {
    setCurrentTab(newValue);
  };

  const handleStudentFormChange = (e) => {
    const { name, value, checked } = e.target;
    setStudentForm(prev => ({
      ...prev,
      [name]: name === 'isOneOnOne' ? checked : value
    }));
  };

  const handleStudentSubmit = (e) => {
    e.preventDefault();
    if (!studentForm.name || !studentForm.day || !studentForm.time) {
      alert('모든 필드를 입력해주세요.');
      return;
    }

    const newStudent = {
      id: Date.now().toString(),
      ...studentForm
    };

    const updatedStudents = [...students, newStudent];
    setStudents(updatedStudents);
    localStorage.setItem('students', JSON.stringify(updatedStudents));

    setStudentForm({
      name: '',
      day: '',
      time: '',
      isOneOnOne: false
    });

    alert('학생이 등록되었습니다.');
  };

  const handleDeleteStudent = (studentId) => {
    if (window.confirm('이 학생을 삭제하시겠습니까?')) {
      const updatedStudents = students.filter(student => student.id !== studentId);
      setStudents(updatedStudents);
      localStorage.setItem('students', JSON.stringify(updatedStudents));
    }
  };

  const handleEndDay = () => {
    if (window.confirm('오늘 수업을 종료하시겠습니까?')) {
      dispatch(endDay());
      navigate('/');
    }
  };

  const handleEndWeek = () => {
    const today = new Date().getDay();
    if (today === 6) { // 토요일인 경우
      if (window.confirm('이번 주 수업을 종료하시겠습니까?')) {
        dispatch(endWeek());
        navigate('/');
      }
    } else {
      alert('주간 마감은 토요일에만 가능합니다.');
    }
  };

  const getStudentsByDay = (day) => {
    return students
      .filter(student => student.day === day)
      .sort((a, b) => {
        const timeA = a.time.split(':').map(Number);
        const timeB = b.time.split(':').map(Number);
        return (timeA[0] * 60 + timeA[1]) - (timeB[0] * 60 + timeB[1]);
      });
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper sx={{ p: 3, minHeight: '80vh' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3, gap: 2 }}>
          <IconButton 
            onClick={() => navigate('/')}
            sx={{ 
              background: 'linear-gradient(45deg, #405DE6, #5851DB)',
              color: 'white',
              '&:hover': {
                background: 'linear-gradient(45deg, #5851DB, #833AB4)',
              }
            }}
          >
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h5" component="h1">
            관리 메뉴
          </Typography>
        </Box>

        <Tabs
          value={currentTab}
          onChange={handleTabChange}
          variant="fullWidth"
          sx={{ mb: 3 }}
        >
          <Tab label="학생 등록" />
          <Tab label="학생 목록" />
          <Tab label="수업 종료" />
        </Tabs>

        {currentTab === 0 && (
          <Box component="form" onSubmit={handleStudentSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              name="name"
              label="학생 이름"
              value={studentForm.name}
              onChange={handleStudentFormChange}
              fullWidth
            />

            <FormControl fullWidth>
              <InputLabel>요일</InputLabel>
              <Select
                name="day"
                value={studentForm.day}
                onChange={handleStudentFormChange}
                label="요일"
              >
                {DAYS.map(day => (
                  <MenuItem key={day} value={day}>{day}요일</MenuItem>
                ))}
              </Select>
            </FormControl>

            <FormControl fullWidth>
              <InputLabel>시간</InputLabel>
              <Select
                name="time"
                value={studentForm.time}
                onChange={handleStudentFormChange}
                label="시간"
              >
                {TIMES.map(time => (
                  <MenuItem key={time} value={time}>{time}</MenuItem>
                ))}
              </Select>
            </FormControl>

            <FormControlLabel
              control={
                <Checkbox
                  name="isOneOnOne"
                  checked={studentForm.isOneOnOne}
                  onChange={handleStudentFormChange}
                />
              }
              label="1:1 수업"
            />

            <Button 
              type="submit" 
              variant="contained" 
              sx={{ 
                background: 'linear-gradient(45deg, #405DE6, #5851DB)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #5851DB, #833AB4)',
                }
              }}
            >
              등록하기
            </Button>
          </Box>
        )}

        {currentTab === 1 && (
          <Box sx={{ flex: 1, overflow: 'auto' }}>
            {DAYS.map(day => {
              const dayStudents = getStudentsByDay(day);
              return dayStudents.length > 0 ? (
                <Box key={day} sx={{ mb: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    {day}요일
                  </Typography>
                  <List>
                    {dayStudents.map(student => (
                      <ListItem key={student.id}>
                        <ListItemText
                          primary={student.name}
                          secondary={`${student.time} ${student.isOneOnOne ? '(1:1)' : ''}`}
                        />
                        <ListItemSecondaryAction>
                          <IconButton
                            edge="end"
                            aria-label="delete"
                            onClick={() => handleDeleteStudent(student.id)}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </ListItemSecondaryAction>
                      </ListItem>
                    ))}
                  </List>
                  <Divider />
                </Box>
              ) : null;
            })}
          </Box>
        )}

        {currentTab === 2 && (
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              variant="contained"
              onClick={handleEndDay}
              fullWidth
              sx={{ 
                background: 'linear-gradient(45deg, #405DE6, #5851DB)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #5851DB, #833AB4)',
                }
              }}
            >
              퇴근하기
            </Button>
            <Button
              variant="contained"
              onClick={handleEndWeek}
              fullWidth
              sx={{ 
                background: 'linear-gradient(45deg, #C13584, #E1306C)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #E1306C, #FD1D1D)',
                }
              }}
            >
              주간마감
            </Button>
          </Box>
        )}
      </Paper>
    </Container>
  );
}

export default ManagementPanel;
