import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormControlLabel,
  Checkbox,
  Button,
  Divider,
  Tabs,
  Tab,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Container,
  Paper
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import { ArrowBack as ArrowBackIcon } from '@mui/icons-material';
import { useDispatch } from 'react-redux';
import { endDay, endWeek } from '../../redux/slices/attendanceSlice';
import { useNavigate } from 'react-router-dom';

const DAYS = ['월', '화', '수', '목', '금', '토'];
const TIMES = Array.from({ length: 12 }, (_, i) => {
  const hour = Math.floor(i / 2) + 14; // 14:00부터 시작
  const minute = i % 2 === 0 ? '00' : '30';
  return `${hour}:${minute}`;
});

function ManagementPanel() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [currentTab, setCurrentTab] = useState(0);
  const [students, setStudents] = useState([]);
  const [studentForm, setStudentForm] = useState({
    name: '',
    day: '',
    time: '',
    isOneOnOne: false
  });

  useEffect(() => {
    const savedStudents = JSON.parse(localStorage.getItem('students') || '[]');
    setStudents(savedStudents);
  }, []);

  const handleTabChange = (event, newValue) => {
    setCurrentTab(newValue);
  };

  const handleStudentFormChange = (e) => {
    const { name, value, checked } = e.target;
    setStudentForm(prev => ({
      ...prev,
      [name]: name === 'isOneOnOne' ? checked : value
    }));
  };

  const handleStudentSubmit = (e) => {
    e.preventDefault();
    if (!studentForm.name || !studentForm.day || !studentForm.time) {
      alert('모든 필드를 입력해주세요.');
      return;
    }

    const newStudent = {
      id: Date.now().toString(),
      ...studentForm
    };

    const updatedStudents = [...students, newStudent];
    setStudents(updatedStudents);
    localStorage.setItem('students', JSON.stringify(updatedStudents));

    setStudentForm({
      name: '',
      day: '',
      time: '',
      isOneOnOne: false
    });

    alert('학생이 등록되었습니다.');
  };

  const handleDeleteStudent = (studentId) => {
    if (window.confirm('이 학생을 삭제하시겠습니까?')) {
      const updatedStudents = students.filter(student => student.id !== studentId);
      setStudents(updatedStudents);
      localStorage.setItem('students', JSON.stringify(updatedStudents));
    }
  };

  const handleEndDay = () => {
    if (window.confirm('오늘 수업을 종료하시겠습니까?')) {
      dispatch(endDay());
      navigate('/');
    }
  };

  const handleEndWeek = () => {
    const today = new Date().getDay();
    if (today === 6) { // 토요일인 경우
      if (window.confirm('이번 주 수업을 종료하시겠습니까?')) {
        dispatch(endWeek());
        navigate('/');
      }
    } else {
      alert('주간 마감은 토요일에만 가능합니다.');
    }
  };

  const handleResetAttendance = () => {
    if (window.confirm('출석 인원을 초기화하시겠습니까?')) {
      dispatch(endDay());
      alert('출석 인원이 초기화되었습니다.');
    }
  };

  const getStudentsByDay = (day) => {
    return students
      .filter(student => student.day === day)
      .sort((a, b) => {
        const timeA = a.time.split(':').map(Number);
        const timeB = b.time.split(':').map(Number);
        return (timeA[0] * 60 + timeA[1]) - (timeB[0] * 60 + timeB[1]);
      });
  };

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: '#FAFAFA', p: 2 }}>
      {/* Instagram-style Header */}
      <Paper 
        elevation={1}
        sx={{
          py: 2,
          px: 3,
          borderRadius: 0,
          borderBottom: '1px solid #DBDBDB',
          display: 'flex',
          alignItems: 'center',
          bgcolor: 'white',
          position: 'sticky',
          top: 0,
          zIndex: 1000,
          mb: 3,
        }}
      >
        <IconButton
          onClick={() => navigate('/')}
          sx={{ mr: 2 }}
        >
          <ArrowBackIcon />
        </IconButton>
        <Typography 
          variant="h6" 
          sx={{ 
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            fontWeight: 600,
          }}
        >
          관리
        </Typography>
      </Paper>

      {/* Management Content */}
      <Paper
        elevation={0}
        sx={{
          p: 3,
          borderRadius: 3,
          bgcolor: 'white',
          border: '1px solid #DBDBDB',
          maxWidth: 600,
          mx: 'auto',
        }}
      >
        <Tabs
          value={currentTab}
          onChange={handleTabChange}
          variant="fullWidth"
          sx={{ 
            mb: 3,
            '& .MuiTab-root': {
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              fontWeight: 600,
              fontSize: '0.9rem',
              textTransform: 'none',
            }
          }}
        >
          <Tab label="학생 등록" />
          <Tab label="학생 목록" />
          <Tab label="수업 관리" />
        </Tabs>

        {currentTab === 0 && (
          <Box component="form" onSubmit={handleStudentSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              name="name"
              label="학생 이름"
              value={studentForm.name}
              onChange={handleStudentFormChange}
              fullWidth
              sx={{ 
                '& .MuiInputLabel-root': {
                  fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                }
              }}
            />

            <FormControl fullWidth>
              <InputLabel>요일</InputLabel>
              <Select
                name="day"
                value={studentForm.day}
                onChange={handleStudentFormChange}
                label="요일"
              >
                {DAYS.map(day => (
                  <MenuItem key={day} value={day}>{day}요일</MenuItem>
                ))}
              </Select>
            </FormControl>

            <FormControl fullWidth>
              <InputLabel>시간</InputLabel>
              <Select
                name="time"
                value={studentForm.time}
                onChange={handleStudentFormChange}
                label="시간"
              >
                {TIMES.map(time => (
                  <MenuItem key={time} value={time}>{time}</MenuItem>
                ))}
              </Select>
            </FormControl>

            <FormControlLabel
              control={
                <Checkbox
                  name="isOneOnOne"
                  checked={studentForm.isOneOnOne}
                  onChange={handleStudentFormChange}
                />
              }
              label="1:1 수업"
              sx={{
                '& .MuiFormControlLabel-label': {
                  fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                }
              }}
            />

            <Button 
              type="submit" 
              variant="contained" 
              sx={{
                py: 1.5,
                background: 'linear-gradient(45deg, #405DE6, #5851DB)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #5851DB, #833AB4)',
                },
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                fontWeight: 600,
                textTransform: 'none',
              }}
            >
              등록하기
            </Button>
          </Box>
        )}

        {currentTab === 1 && (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
            {DAYS.map(day => {
              const dayStudents = getStudentsByDay(day);
              return dayStudents.length > 0 ? (
                <Box key={day}>
                  <Typography 
                    variant="h6" 
                    gutterBottom
                    sx={{
                      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                      fontWeight: 600,
                      color: '#262626',
                    }}
                  >
                    {day}요일
                  </Typography>
                  <List>
                    {dayStudents.map(student => (
                      <ListItem 
                        key={student.id}
                        sx={{
                          borderBottom: '1px solid #DBDBDB',
                          py: 1.5,
                        }}
                      >
                        <ListItemText
                          primary={student.name}
                          secondary={`${student.time} ${student.isOneOnOne ? '(1:1)' : ''}`}
                          sx={{
                            '& .MuiListItemText-primary': {
                              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                              fontWeight: 600,
                              color: '#262626',
                            },
                            '& .MuiListItemText-secondary': {
                              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                              color: '#8e8e8e',
                            },
                          }}
                        />
                        <ListItemSecondaryAction>
                          <IconButton
                            edge="end"
                            onClick={() => handleDeleteStudent(student.id)}
                            sx={{
                              color: '#ed4956',
                            }}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </ListItemSecondaryAction>
                      </ListItem>
                    ))}
                  </List>
                </Box>
              ) : null;
            })}
          </Box>
        )}

        {currentTab === 2 && (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <Button
              variant="contained"
              onClick={handleResetAttendance}
              sx={{
                py: 2,
                borderRadius: 2,
                fontSize: '1rem',
                fontWeight: 600,
                textTransform: 'none',
                background: 'linear-gradient(45deg, #405DE6, #5851DB)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #5851DB, #833AB4)',
                },
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              }}
            >
              출석 인원 초기화
            </Button>

            <Button
              variant="contained"
              onClick={handleEndDay}
              sx={{
                py: 2,
                borderRadius: 2,
                fontSize: '1rem',
                fontWeight: 600,
                textTransform: 'none',
                background: 'linear-gradient(45deg, #833AB4, #C13584)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #C13584, #E1306C)',
                },
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              }}
            >
              오늘 수업 종료
            </Button>

            <Button
              variant="contained"
              onClick={handleEndWeek}
              sx={{
                py: 2,
                borderRadius: 2,
                fontSize: '1rem',
                fontWeight: 600,
                textTransform: 'none',
                background: 'linear-gradient(45deg, #C13584, #E1306C)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #E1306C, #FD1D1D)',
                },
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              }}
            >
              이번 주 수업 종료
            </Button>
          </Box>
        )}
      </Paper>
    </Box>
  );
}

export default ManagementPanel;
