import React from 'react';
import { Box, Button, IconButton, Paper, Typography } from '@mui/material';
import { Settings as SettingsIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { markAttendance } from '../../redux/slices/attendanceSlice';
import { format } from 'date-fns';
import { ko } from 'date-fns/locale';

function AttendanceButton() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const todayCount = useSelector(state => state.attendance.todayCount);
  const currentTime = format(new Date(), 'a h:mm', { locale: ko });
  const currentDate = format(new Date(), 'PPP', { locale: ko });

  const handleAttendanceCheck = () => {
    dispatch(markAttendance());
  };

  return (
    <Box 
      sx={{ 
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        bgcolor: '#FAFAFA'
      }}
    >
      {/* Instagram-style Header */}
      <Paper 
        elevation={1}
        sx={{
          py: 2,
          px: 3,
          borderRadius: 0,
          borderBottom: '1px solid #DBDBDB',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          bgcolor: 'white',
          position: 'sticky',
          top: 0,
          zIndex: 1000,
        }}
      >
        <Typography 
          variant="h6" 
          sx={{ 
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            fontWeight: 700,
            background: 'linear-gradient(45deg, #405DE6, #5851DB, #833AB4, #C13584, #E1306C, #FD1D1D)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
          }}
        >
          Kevin's Class
        </Typography>
        <IconButton
          onClick={() => navigate('/management')}
          sx={{
            color: '#262626',
          }}
        >
          <SettingsIcon />
        </IconButton>
      </Paper>

      {/* Main Content */}
      <Box sx={{ flex: 1, p: 2 }}>
        <Paper 
          elevation={0}
          sx={{
            p: 3,
            borderRadius: 3,
            bgcolor: 'white',
            border: '1px solid #DBDBDB',
            maxWidth: 600,
            mx: 'auto',
          }}
        >
          {/* Date and Time Display */}
          <Box sx={{ mb: 4, textAlign: 'center' }}>
            <Typography 
              variant="h6" 
              sx={{ 
                color: '#262626',
                fontWeight: 600,
                mb: 1,
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              }}
            >
              {currentDate}
            </Typography>
            <Typography 
              variant="h4" 
              sx={{ 
                color: '#262626',
                fontWeight: 700,
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              }}
            >
              {currentTime}
            </Typography>
          </Box>

          {/* Attendance Button */}
          <Box sx={{ textAlign: 'center', mb: 4 }}>
            <Button
              variant="contained"
              onClick={handleAttendanceCheck}
              sx={{
                px: 6,
                py: 2,
                borderRadius: 3,
                fontSize: '1.1rem',
                fontWeight: 600,
                textTransform: 'none',
                background: 'linear-gradient(45deg, #405DE6, #5851DB, #833AB4, #C13584, #E1306C, #FD1D1D)',
                '&:hover': {
                  background: 'linear-gradient(45deg, #833AB4, #C13584, #E1306C, #FD1D1D, #405DE6, #5851DB)',
                },
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              }}
            >
              출석체크
            </Button>
          </Box>

          {/* Attendance Count */}
          <Box 
            sx={{ 
              textAlign: 'center',
              p: 2,
              borderRadius: 2,
              bgcolor: '#FAFAFA',
              border: '1px solid #DBDBDB',
            }}
          >
            <Typography 
              variant="h5" 
              sx={{ 
                color: '#262626',
                fontWeight: 600,
                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
              }}
            >
              오늘 출석: {todayCount}회
            </Typography>
          </Box>
        </Paper>
      </Box>
    </Box>
  );
}

export default AttendanceButton;
