import React from 'react';
import { Box, IconButton, Typography, Button, List, ListItem, ListItemText, Chip, Paper } from '@mui/material';
import SettingsIcon from '@mui/icons-material/Settings';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { markAttendance, selectAttendedStudents } from '../../redux/slices/attendanceSlice';

const PASSWORD = '1234'; // 관리자 비밀번호

const AttendanceButton = ({ student }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const now = new Date();
  const currentDate = now.toLocaleDateString('ko-KR', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long'
  });
  const currentTime = now.toLocaleTimeString('ko-KR', {
    hour: '2-digit',
    minute: '2-digit',
    hour12: true
  });

  const attendedStudents = useSelector(selectAttendedStudents);

  const handleAttendance = () => {
    dispatch(markAttendance(student));
  };

  const handleSettings = () => {
    navigate('/management');
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: 'background.default',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* Header */}
      <Box
        sx={{
          width: '100%',
          bgcolor: 'background.paper',
          borderBottom: '1px solid #DBDBDB',
          py: 2,
          px: 3,
          position: 'sticky',
          top: 0,
          zIndex: 1000,
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Box
            component="img"
            src="/11.png"
            alt="DLAB Logo"
            sx={{
              height: 32,
              width: 'auto',
              objectFit: 'contain',
            }}
          />
          <Typography
            variant="h6"
            sx={{
              color: 'primary.main',
              fontWeight: 600,
            }}
          >
            Kevin's Class
          </Typography>
        </Box>
        <IconButton
          onClick={handleSettings}
          sx={{ color: 'secondary.main' }}
        >
          <SettingsIcon />
        </IconButton>
      </Box>

      {/* Date and Time Display */}
      <Box
        sx={{
          width: '100%',
          bgcolor: 'background.paper',
          py: 3,
          px: 3,
          borderBottom: '1px solid #DBDBDB',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: 1,
        }}
      >
        <Typography
          variant="h6"
          sx={{
            color: 'text.primary',
            fontWeight: 600,
          }}
        >
          {currentDate}
        </Typography>
        <Typography
          sx={{
            color: 'text.secondary',
          }}
        >
          {currentTime}
        </Typography>
      </Box>

      {/* Main Content */}
      <Box
        sx={{
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          p: 3,
          gap: 3,
        }}
      >
        <Paper
          elevation={0}
          sx={{
            p: 3,
            width: '100%',
            maxWidth: 400,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: 2,
            border: '1px solid #DBDBDB',
            borderRadius: 2,
          }}
        >
          <Typography
            variant="h5"
            sx={{
              color: 'text.primary',
              fontWeight: 600,
              textAlign: 'center',
            }}
          >
            {student.name}
          </Typography>
          <Typography
            sx={{
              color: 'text.secondary',
              textAlign: 'center',
            }}
          >
            {student.day}요일 {student.time}
          </Typography>
          {student.isOneOnOne && (
            <Chip
              label="1:1"
              size="small"
              sx={{
                bgcolor: 'primary.main',
                color: 'primary.contrastText',
                fontWeight: 600,
              }}
            />
          )}
        </Paper>

        <Button
          variant="contained"
          onClick={handleAttendance}
          disabled={attendedStudents.includes(student.id)}
          sx={{
            width: '100%',
            maxWidth: 400,
            py: 2,
            bgcolor: !attendedStudents.includes(student.id)
              ? 'primary.main'
              : 'grey.300',
            color: !attendedStudents.includes(student.id)
              ? 'primary.contrastText'
              : 'text.secondary',
            '&:hover': {
              bgcolor: !attendedStudents.includes(student.id)
                ? 'primary.dark'
                : 'grey.300',
            },
          }}
        >
          {attendedStudents.includes(student.id) ? '출석완료' : '출석'}
        </Button>
      </Box>
    </Box>
  );
};

export default AttendanceButton;
