import React from 'react';
import { Box, Button, IconButton, Paper, Typography } from '@mui/material';
import { Settings as SettingsIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { markAttendance } from '../../redux/slices/attendanceSlice';
import { format } from 'date-fns';
import { ko } from 'date-fns/locale';

function AttendanceButton() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const todayCount = useSelector(state => state.attendance.todayCount);
  const currentTime = format(new Date(), 'a h:mm', { locale: ko });
  const currentDate = format(new Date(), 'PPP', { locale: ko });

  const handleAttendanceCheck = () => {
    dispatch(markAttendance());
  };

  return (
    <Box sx={{ textAlign: 'center' }}>
      <Paper 
        elevation={3}
        sx={{
          p: 4,
          borderRadius: 2,
          background: 'white',
          position: 'relative'
        }}
      >
        <IconButton
          sx={{
            position: 'absolute',
            top: 8,
            right: 8,
          }}
          onClick={() => navigate('/management')}
        >
          <SettingsIcon />
        </IconButton>

        <Typography variant="h6" sx={{ mb: 1, color: 'text.secondary' }}>
          {currentDate}
        </Typography>

        <Typography variant="h4" sx={{ mb: 3, fontWeight: 'bold' }}>
          {currentTime}
        </Typography>

        <Button
          variant="contained"
          size="large"
          onClick={handleAttendanceCheck}
          sx={{
            px: 6,
            py: 2,
            borderRadius: 3,
            fontSize: '1.2rem',
            fontWeight: 'bold',
            textTransform: 'none',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            '&:hover': {
              boxShadow: '0 6px 8px rgba(0, 0, 0, 0.2)',
            }
          }}
        >
          출석체크
        </Button>

        <Typography 
          variant="h5" 
          sx={{ 
            mt: 3,
            fontWeight: 'bold',
            color: 'primary.main'
          }}
        >
          오늘 출석: {todayCount}회
        </Typography>
      </Paper>
    </Box>
  );
}

export default AttendanceButton;
