import { createSlice } from '@reduxjs/toolkit';
import { format } from 'date-fns';

const loadState = () => {
  try {
    const serializedState = localStorage.getItem('attendanceState');
    if (serializedState === null) {
      return undefined;
    }
    return JSON.parse(serializedState);
  } catch (err) {
    return undefined;
  }
};

const saveState = (state) => {
  try {
    const serializedState = JSON.stringify(state);
    localStorage.setItem('attendanceState', serializedState);
    // 출석 기록 히스토리 저장
    localStorage.setItem('attendanceHistory', JSON.stringify(state.history));
  } catch (err) {
    // Ignore write errors
  }
};

const initialState = {
  ...(loadState() || {
    todayCount: 0,
    weeklyCount: 0,
    history: {},
    attendedStudents: [],
    weeklyAttendance: {
      '월': 0,
      '화': 0,
      '수': 0,
      '목': 0,
      '금': 0,
      '토': 0,
    }
  }),
  attendedStudents: loadState()?.attendedStudents || []
};

const attendanceSlice = createSlice({
  name: 'attendance',
  initialState,
  reducers: {
    markAttendance: (state, action) => {
      const student = action.payload;

      // 이미 출석한 학생인지 확인
      if (!state.attendedStudents.includes(student.id)) {
        // 1:1 수업은 2명, 일반 수업은 1명으로 카운트
        const increment = student.isOneOnOne ? 2 : 1;

        state.todayCount = (state.todayCount || 0) + increment;
        state.weeklyCount = (state.weeklyCount || 0) + increment;

        // 출석한 학생 ID 저장
        state.attendedStudents.push(student.id);

        const dateKey = new Date().toISOString().split('T')[0];
        if (!state.history) state.history = {};
        state.history[dateKey] = state.todayCount;

        const today = new Date().getDay();
        const days = ['일', '월', '화', '수', '목', '금', '토'];
        const dayName = days[today];
        state.weeklyAttendance[dayName] = (state.weeklyAttendance[dayName] || 0) + increment;

        saveState(state);
      }
    },
    endDay: (state) => {
      state.todayCount = 0;
      state.attendedStudents = []; // 출석 학생 목록 초기화
      saveState(state);
    },
    endWeek: (state) => {
      state.todayCount = 0;
      state.weeklyCount = 0;
      state.history = {};
      state.attendedStudents = []; // 출석 학생 목록 초기화
      state.weeklyAttendance = {
        '월': 0,
        '화': 0,
        '수': 0,
        '목': 0,
        '금': 0,
        '토': 0,
      };
      state.lastReset = new Date().toISOString();
      saveState(state);
    },
  },
});

export const selectTodayCount = state => state.attendance.todayCount;
export const selectWeeklyCount = state => state.attendance.weeklyCount;
export const selectWeeklyAttendance = state => state.attendance.weeklyAttendance;
export const selectAttendedStudents = state => state.attendance.attendedStudents || [];

export const { markAttendance, endDay, endWeek } = attendanceSlice.actions;

// 미들웨어: 매주 월요일 자동 초기화
export const weeklyResetMiddleware = store => next => action => {
  const result = next(action);

  const state = store.getState();
  const lastReset = state.attendance.lastReset ? new Date(state.attendance.lastReset) : null;
  const now = new Date();

  if (lastReset) {
    const daysSinceReset = Math.floor((now - lastReset) / (1000 * 60 * 60 * 24));
    const isMonday = now.getDay() === 1;

    if (daysSinceReset >= 7 && isMonday) {
      store.dispatch(endWeek());
    }
  }

  return result;
};

export default attendanceSlice.reducer;
