import React from 'react';
import { Box, IconButton, Typography, Button, List, ListItem, ListItemText, Chip } from '@mui/material';
import SettingsIcon from '@mui/icons-material/Settings';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { markAttendance, selectAttendedStudents } from '../../redux/slices/attendanceSlice';

const PASSWORD = '1234'; // 관리자 비밀번호

function AttendanceButton() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const todayCount = useSelector(state => state.attendance.todayCount || 0);
  const attendedStudents = useSelector(selectAttendedStudents);
  const students = JSON.parse(localStorage.getItem('students') || '[]');

  // 현재 날짜와 시간 정보
  const now = new Date();
  const days = ['일', '월', '화', '수', '목', '금', '토'];
  const currentDay = days[now.getDay()];
  const currentDate = `${now.getFullYear()}년 ${now.getMonth() + 1}월 ${now.getDate()}일`;
  const currentTime = now.toLocaleTimeString('ko-KR', {
    hour: '2-digit',
    minute: '2-digit',
    hour12: true
  });

  // 오늘의 학생 목록
  const todayStudents = students
    .filter(student => student.day === currentDay)
    .sort((a, b) => {
      const timeA = a.time.split(':').map(Number);
      const timeB = b.time.split(':').map(Number);
      return (timeA[0] * 60 + timeA[1]) - (timeB[0] * 60 + timeB[1]);
    });

  // 현재 시간이 수업 시간 전후 30분 이내인지 확인하는 함수
  const isWithinClassTime = (classTime) => {
    const [hours, minutes] = classTime.split(':').map(Number);
    const classTimeMinutes = hours * 60 + minutes;
    const currentHours = now.getHours();
    const currentMinutes = now.getMinutes();
    const currentTotalMinutes = currentHours * 60 + currentMinutes;

    return Math.abs(currentTotalMinutes - classTimeMinutes) <= 30;
  };

  const handleSettingsClick = () => {
    const password = prompt('비밀번호를 입력하세요:');
    if (password === PASSWORD) {
      navigate('/management');
    } else if (password !== null) {
      alert('비밀번호가 올바르지 않습니다.');
    }
  };

  const handleAttendanceClick = (student) => {
    if (!isWithinClassTime(student.time)) {
      alert('수업 시간 30분 전후에만 출석체크가 가능합니다.');
      return;
    }

    if (window.confirm('출석을 체크하시겠습니까?')) {
      dispatch(markAttendance(student));
      alert(`${student.name} 학생 출석이 완료되었습니다.${student.isOneOnOne ? ' (1:1 수업)' : ''}`);
    }
  };

  return (
    <Box 
      sx={{ 
        minHeight: '100vh',
        bgcolor: '#FAFAFA',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        position: 'relative',
        pt: 2
      }}
    >
      {/* Header */}
      <Box
        sx={{
          width: '100%',
          bgcolor: 'white',
          borderBottom: '1px solid #DBDBDB',
          py: 2,
          px: 3,
          position: 'sticky',
          top: 0,
          zIndex: 1000,
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        <Typography
          variant="h6"
          sx={{
            background: 'linear-gradient(45deg, #405DE6, #5851DB, #833AB4, #C13584, #E1306C, #FD1D1D)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 600,
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
          }}
        >
          Kevin's Class
        </Typography>
        <IconButton 
          onClick={handleSettingsClick}
          sx={{
            color: '#262626',
          }}
        >
          <SettingsIcon />
        </IconButton>
      </Box>

      {/* Main Content */}
      <Box
        sx={{
          width: '100%',
          maxWidth: 600,
          p: 2,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: 3,
          mt: 3,
        }}
      >
        {/* Date and Time Display */}
        <Box
          sx={{
            width: '100%',
            p: 3,
            bgcolor: 'white',
            borderRadius: 3,
            border: '1px solid #DBDBDB',
            textAlign: 'center',
          }}
        >
          <Typography
            variant="h6"
            sx={{
              fontWeight: 600,
              color: '#262626',
              mb: 1,
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            }}
          >
            {currentDate} ({currentDay}요일)
          </Typography>
          <Typography
            variant="h4"
            sx={{
              fontWeight: 700,
              color: '#262626',
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            }}
          >
            {currentTime}
          </Typography>
        </Box>

        {/* Today's Students List */}
        <Box
          sx={{
            width: '100%',
            bgcolor: 'white',
            borderRadius: 3,
            border: '1px solid #DBDBDB',
            overflow: 'hidden',
          }}
        >
          <Typography
            sx={{
              p: 2,
              borderBottom: '1px solid #DBDBDB',
              fontWeight: 600,
              color: '#262626',
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            }}
          >
            오늘의 수업
          </Typography>
          <List>
            {todayStudents.map((student) => (
              <ListItem
                key={student.id}
                sx={{
                  borderBottom: '1px solid #DBDBDB',
                  '&:last-child': {
                    borderBottom: 'none',
                  },
                }}
              >
                <ListItemText
                  primary={student.name}
                  secondary={student.time}
                  sx={{
                    '& .MuiListItemText-primary': {
                      fontWeight: 600,
                      color: '#262626',
                      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                    },
                    '& .MuiListItemText-secondary': {
                      color: '#8e8e8e',
                      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                    },
                  }}
                />
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  {student.isOneOnOne && (
                    <Chip
                      label="1:1"
                      size="small"
                      sx={{
                        bgcolor: '#E1306C',
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                      }}
                    />
                  )}
                  <Button
                    variant="contained"
                    onClick={() => handleAttendanceClick(student)}
                    disabled={!isWithinClassTime(student.time) || attendedStudents.includes(student.id)}
                    sx={{
                      background: isWithinClassTime(student.time) && !attendedStudents.includes(student.id)
                        ? 'linear-gradient(45deg, #405DE6, #5851DB)'
                        : '#DBDBDB',
                      '&:hover': {
                        background: isWithinClassTime(student.time) && !attendedStudents.includes(student.id)
                          ? 'linear-gradient(45deg, #5851DB, #833AB4)'
                          : '#DBDBDB',
                      },
                      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                      fontWeight: 600,
                      textTransform: 'none',
                    }}
                  >
                    {attendedStudents.includes(student.id) ? '출석완료' : '출석'}
                  </Button>
                </Box>
              </ListItem>
            ))}
            {todayStudents.length === 0 && (
              <ListItem>
                <ListItemText
                  primary="오늘은 예정된 수업이 없습니다."
                  sx={{
                    textAlign: 'center',
                    color: '#8e8e8e',
                    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
                  }}
                />
              </ListItem>
            )}
          </List>
        </Box>

        {/* Attendance Count */}
        <Box
          sx={{
            width: '100%',
            p: 3,
            bgcolor: 'white',
            borderRadius: 3,
            border: '1px solid #DBDBDB',
            textAlign: 'center',
          }}
        >
          <Typography
            variant="h5"
            sx={{
              fontWeight: 600,
              color: '#262626',
              fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
            }}
          >
            오늘 출석: {todayCount}명
          </Typography>
        </Box>
      </Box>
    </Box>
  );
}

export default AttendanceButton;
